import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { CSSTransition } from 'react-transition-group';
import { actionCreators }  from './store';
import { actionCreators as loginActionCreators }  from '../../pages/login/store';
import { 
  HeaderWrapper, 
  Logo, 
  Nav, 
  NavItem, 
  SearchWrapper,
  NavA, 
  NavSearch,
  SearchInfo,
  SearchInfoTitle,
  SearchInfoSwitch,
  SearchInfoList,
  SearchInfoItem, 
  Addition, 
  Button
} from './style';

class Header extends Component {

  getListArea () {
    const { focused, list, page, totalPage, mouseIn, handleMouseEnter, handleMouseLeave, handleChangePage } = this.props;
    const newList = list.toJS();
    const pageList = [];

    if (newList.length) {
      for (let i = (page - 1) * 10; i < page * 10; i++) {
        pageList.push(
          <SearchInfoItem key= {newList[i]}>{newList[i]}</SearchInfoItem>
        )
      }
    }  

    if(focused || mouseIn) {
        return (
          <SearchInfo 
              onMouseEnter={ handleMouseEnter }
              onMouseLeave={ handleMouseLeave }
          >
              <SearchInfoTitle>
                  热门搜索
                  <SearchInfoSwitch 
                     onClick={() => handleChangePage(page,totalPage, this.spinIcon)}
                  >
                    <span ref={(icon)=>{this.spinIcon = icon}} className='iconfont spin'>&#xe606;</span>
                    换一批
                  </SearchInfoSwitch>
              </SearchInfoTitle>
              <SearchInfoList>
                 { pageList }
              </SearchInfoList>
          </SearchInfo>
        )
      }else {
         return null;
    }
  }

  render () {
    const { logout, login, focused, handleInputFocus, handleInputBlur, list } = this.props;
     return (
      <HeaderWrapper>
          <Link to='/'>
            <Logo />
          </Link>
          <Nav>
            <NavItem className='left active'>首页</NavItem>
            <NavItem className='left'>下载App</NavItem>
            <SearchWrapper>
              <CSSTransition
                in={focused}
                timeout={ 300 }
                classNames="slide"
              >
                  <NavSearch
                    className = {focused ? 'focused': ''}         
                    onFocus   = {() => handleInputFocus(list)}
                    onBlur    = {handleInputBlur}
                  >
                  </NavSearch>
              </CSSTransition> 
              <span className= {focused ? 'focused iconfont zoom': 'iconfont zoom'} >
                &#xe62d;
              </span>
              {this.getListArea()}       
            </SearchWrapper>
            {
              login ? 
                  <NavItem onClick={logout} className='right'>退出</NavItem> : 
                  <Link to='/login'><NavItem className='right'>登录</NavItem></Link>
            }
            <NavA />
            <NavItem className='right'>
                <span className='iconfont'>&#xe636;</span>
            </NavItem>   
          </Nav>
         <Addition>
             <Link to='/write'>
               <Button className="writting">
                 <span className="iconfont">&#xe60e;</span>写文章
               </Button>
             </Link> 
             <Button className= "reg">注册</Button> 
          </Addition>                       
        </HeaderWrapper> 
     );
   }
}

const mapStateToProps = (state) => {
  return {
    focused: state.getIn(['header', 'focused']),
    list: state.getIn(['header', 'list']),
    page: state.getIn(['header', 'page']),
    totalPage: state.getIn(['header', 'totalPage']),
    mouseIn: state.getIn(['header', 'mouseIn']),
    login: state.getIn(['login', 'login'])
  }
}

const mapDispathToProps = (dispatch) => {
  return {
    handleInputFocus(list) {
      (list.size === 0)&& dispatch(actionCreators.getList()); 
      dispatch(actionCreators.searchFocus());
    },
    handleInputBlur() {
      dispatch(actionCreators.searchBlur());
    },
    handleMouseEnter() {
      dispatch(actionCreators.mouseEnter());
    },
    handleMouseLeave() {
      dispatch(actionCreators.mouseLeave());
    },
    handleChangePage(page, totalPage, spin) {
      let originAngle = spin.style.transform.replace(/[^0-9]/ig, '');
      if(originAngle) {
        originAngle = parseInt(originAngle , 10);
      }else {
        originAngle = 0;
      }
      spin.style.transform = 'rotate('+ ( originAngle + 360 ) +'deg)';

      if(page < totalPage) {
        dispatch(actionCreators.changePage(page + 1));
      }else {
        dispatch(actionCreators.changePage(1));
        }
      },
      logout() {
        dispatch(loginActionCreators.logout())
    }
  }
} 

export default connect(mapStateToProps, mapDispathToProps)(Header);
